// Parse NVD CVE data
const nvdData = $input.first().json;
const items = [];

if (nvdData.vulnerabilities) {
  for (const vuln of nvdData.vulnerabilities.slice(0, 20)) {
    const cve = vuln.cve;
    const cveId = cve.id;
    const published = cve.published;
    const description = cve.descriptions?.find(d => d.lang === 'en')?.value || 'No description';

    // Get CVSS score
    const metrics = cve.metrics || {};
    const cvssV31 = metrics.cvssMetricV31?.[0]?.cvssData;
    const cvssV30 = metrics.cvssMetricV30?.[0]?.cvssData;
    const cvssData = cvssV31 || cvssV30 || {};
    const cvssScore = cvssData.baseScore || 0;
    const severity = cvssData.baseSeverity || 'UNKNOWN';

    items.push({
      source: 'NVD',
      category: 'CVE',
      severity: severity,
      cvssScore: cvssScore,
      title: cveId,
      description: description.substring(0, 200),
      url: `https://nvd.nist.gov/vuln/detail/${cveId}`,
      publishedDate: published,
      timestamp: new Date(published).getTime()
    });
  }
}

console.log(`Parsed ${items.length} CVEs from NVD`);
return items.map(item => ({json: item}));

