  const oneDayAgo = Date.now() - (24 * 60 * 60 * 1000);

  for (const vuln of cisaData.vulnerabilities) {
    const addedDate = new Date(vuln.dateAdded).getTime();

    if (addedDate > oneDayAgo) {
      items.push({
        source: 'CISA KEV',
