        publishedDate: vuln.dateAdded,
        timestamp: addedDate,
        exploited: true,
        dueDate: vuln.dueDate
      });
    }
  }
}

console.log(`Parsed ${items.length} exploited CVEs from CISA`);
return items.map(item => ({json: item}));

// Parse RSS feed (Krebs)
const xml = $input.first().json;
const items = [];

// Simple XML parsing
const itemRegex = /<item>([\s\S]*?)<\/item>/g;
let match;

while ((match = itemRegex.exec(xml)) !== null) {
  const item = match[1];

  const titleMatch = item.match(/<title><!\[CDATA\[([^\]]+)\]\]><\/title>/);
  const linkMatch = item.match(/<link>([^<]+)<\/link>/);
  const pubDateMatch = item.match(/<pubDate>([^<]+)<\/pubDate>/);
  const descMatch = item.match(/<description><!\[CDATA\[([^\]]+)\]\]><\/description>/);

  if (titleMatch && linkMatch) {
    const pubDate = pubDateMatch ? new Date(pubDateMatch[1]) : new Date();
    const oneDayAgo = Date.now() - (24 * 60 * 60 * 1000);

    if (pubDate.getTime() > oneDayAgo) {
      items.push({
        source: 'Krebs on Security',
        category: 'NEWS',
        severity: 'INFO',
        cvssScore: 0,
        title: titleMatch[1],
        description: descMatch ? descMatch[1].substring(0, 200) : '',
        url: linkMatch[1],
        publishedDate: pubDate.toISOString(),
        timestamp: pubDate.getTime()
      });
    }
  }
}

console.log(`Parsed ${items.length} articles from Krebs`);
return items.map(item => ({json: item}));

