// Input expected from AI Agent: $input.first().json
// Example: { action: "build", tool: "nmap", target: "10.1.1.5", extra: {...} }
// Must return: [{ json: { command: "<shell command>" } }]

// helper functions
function isIPv4(s) {
  return typeof s === "string" && /^\d{1,3}(\.\d{1,3}){3}$/.test(s);
}
function isDomain(s) {
  return typeof s === "string" && /^[a-z0-9.-]+\.[a-z]{2,}$/i.test(s);
}

const input = $input.first().json || {};
const tool = (input.tool || "").toLowerCase();
const target = input.target || input.test_url || input.ipv4 || input.file_url || "";
const extra = input.extra || {};
let cmd = "";

// sanitize (simple)
const safeTarget = target.replace(/[^a-zA-Z0-9\-\._:\/]/g, "");

if (!safeTarget) {
  return [{ json: { error: "no_target_provided" } }];
}

switch(tool) {
  case "amass":
    // domain-based enum
    cmd = `amass enum -d ${safeTarget} -o amass_${safeTarget.replace(/[^\w]/g,"_")}.txt`;
    break;
  case "nmap":
    // default scan: service detection, top ports, tame timing
    // if extra.ports provided, include it
    const ports = extra.ports ? `-p ${extra.ports}` : "";
    cmd = `nmap -sV -T4 --reason ${ports} --open ${safeTarget} -oN nmap_${safeTarget.replace(/[^\w]/g,"_")}.txt`;
    break;
  case "nuclei":
    // vuln scan; extra.severity allowed
    const sev = extra.severity || "high,critical";
    cmd = `nuclei -u ${safeTarget} -severity ${sev} -o nuclei_${safeTarget.replace(/[^\w]/g,"_")}.txt`;
    break;
  case "format_report":
    // accept a JSON file or multiple outputs â€” this just returns a marker for the formatter step
    cmd = `echo FORMAT_REPORT ${safeTarget}`;
    break;
  default:
    // default: treat tool as raw command builder (pass-through)
    cmd = `${tool} ${safeTarget}`;
}

return [{ json: { command: cmd } }];
