// Validate and extract upload metadata
const input = $input.first();
const body = input.json.body;
const headers = input.json.headers;

if (!body.project_id) {
  throw new Error('project_id is required');
}

if (!body.file_name) {
  throw new Error('file_name is required');
}

if (!body.file_content) {
  throw new Error('file_content (base64) is required');
}

// Decode base64 and calculate hash
const crypto = require('crypto');
const fileBuffer = Buffer.from(body.file_content, 'base64');
const fileHash = crypto.createHash('sha256').update(fileBuffer).digest('hex');

// Determine file type from extension
const ext = body.file_name.split('.').pop().toLowerCase();
const fileTypes = {
  'xml': 'nmap_xml',
  'json': 'json_output',
  'txt': 'text_output',
  'log': 'log_file',
  'pcap': 'packet_capture',
  'png': 'screenshot',
  'jpg': 'screenshot'
};

return [{
  json: {
    project_id: body.project_id,
    file_name: body.file_name,
    file_content: body.file_content,
    file_hash: fileHash,
    file_size: fileBuffer.length,
    file_type: body.file_type || fileTypes[ext] || 'unknown',
    mime_type: body.mime_type || 'application/octet-stream',
    source_tool: body.source_tool || 'manual',
    metadata: body.metadata || {},
    upload_id: crypto.randomUUID()
  }
}];

const input = $('Validate Upload').first().json;
const project = $input.first().json;

if (!project || !project.id) {
  throw new Error(`Project not found or inactive: ${input.project_id}`);
}

// Generate MinIO path
const date = new Date();
const year = date.getFullYear();
const month = String(date.getMonth() + 1).padStart(2, '0');
const day = String(date.getDate()).padStart(2, '0');

const minioPath = `${input.project_id}/${year}/${month}/${day}/${input.upload_id}_${input.file_name}`;

return [{
  json: {
    ...input,
    project_name: project.name,
    minio_bucket: 'raw-evidence',
    minio_path: minioPath
  }
}];

// Queue for parsing based on file type
const input = $('Prepare Storage').first().json;
const evidence = $input.first().json;

const parsableTypes = ['nmap_xml', 'json_output', 'nuclei_json'];
const needsParsing = parsableTypes.includes(input.file_type);

return [{
  json: {
    evidence_id: evidence.id,
    project_id: input.project_id,
    file_name: input.file_name,
    file_type: input.file_type,
    file_hash: input.file_hash,
    minio_bucket: input.minio_bucket,
    minio_path: input.minio_path,
    needs_parsing: needsParsing,
    created_at: evidence.created_at
  }
}];
